//import React from "react";
import { ActiveSessionCheck } from "./FocusTimer";

function PercentageBar({ focusTimer, breakTimer, sessionStatus, elapsed }) {
    const percComplete = (elapsed/ActiveSessionCheck(sessionStatus, focusTimer, breakTimer))*100
    return (
        <>
        <div className="row mb-2">
          <div className="col">
            <div className="progress" style={{ height: "20px" }}>
              <div
                className="progress-bar"
                role="progressbar"
                aria-valuemin="0"
                aria-valuemax="100"
                aria-valuenow={percComplete} // TODO: Increase aria-valuenow as elapsed time increases
                style={{ width: "{percComplete}%" }} // TODO: Increase width % as elapsed time increases
              />
            </div>
          </div>
        </div>
        </>
    )
};

export default PercentageBar;
