import React, { useState } from "react";
import classNames from "../utils/class-names";
import useInterval from "../utils/useInterval";
import { checkMax, checkMin, checkHour } from "./sideFunctions/MathEquations";
import { secondsToDuration } from "../utils/duration";
import SessionRunning from "./sideFunctions/FocusTimer";
import PercentageBar from "./components/percentage-bar";
import SessionTracker from "./components/session-tracker";
import PausedTimer from "./components/paused";


function Pomodoro() {
  // Timer starts out paused

  //Click handlers
  const increaseSessionClick = (value, max) => {
    setSessionStates({
      ...sessionStates,
      [value]: sessionStates[value] + checkMax(sessionStates[value], max)
  });
 };

  const decreaseSessionClick = (value, min) => {
    setSessionStates({
      ...sessionStates,
      [value]: sessionStates[value] - checkMin(sessionStates[value], min)
    });
  };

  const stopSession = () => {
    setSessionStates({
      ...initialSessionState
    });
    setIsTimerRunning(false)
  };


  //State object variable
  const initialSessionState = {
    focusTimer: 1500,
    breakTimer: 300,
    elapsed: 0,
    timeRemaining: 1500,
    sessionStatus: "Focusing",
    isStopped: true
  };

  const [sessionStates, setSessionStates] = useState(initialSessionState)
  const [isTimerRunning, setIsTimerRunning] = useState(false);


  useInterval(
    () => {
      // ToDo: Implement what should happen when the timer is running
      // Function that updates elapsed, remaining, and session status
        // Will return object to update sessionStates state object
      const updatedSessionStates = SessionRunning(sessionStates);

      setSessionStates({
        ...sessionStates,
        ...updatedSessionStates
      })
    },
    isTimerRunning ? 1000 : null
  );

  function playPause() {
    setIsTimerRunning((prevState) => !prevState);
    setSessionStates({
      ...sessionStates,
      isStopped: false
    });
  }

  return (
    <div className="pomodoro">
      <div className="row">
        <div className="col">
          <div className="input-group input-group-lg mb-2">
            <span className="input-group-text" data-testid="duration-focus">
              {/* TODO: Update this text to display the current focus session duration */}
              Focus Duration: {checkHour(sessionStates.focusTimer)}
            </span>
            <div className="input-group-append">
              {/* TODO: Implement decreasing focus duration and disable during a focus or break session */}
              <button
                type="button"
                className="btn btn-secondary"
                data-testid="decrease-focus"
                onClick={() => decreaseSessionClick("focusTimer", 300)}
              >
                <span className="oi oi-minus" />
              </button>
              {/* TODO: Implement increasing focus duration  and disable during a focus or break session */}
              <button
                type="button"
                className="btn btn-secondary"
                data-testid="increase-focus"
                onClick={() => increaseSessionClick("focusTimer", 3600)}
              >
                <span className="oi oi-plus" />
              </button>
            </div>
          </div>
        </div>
        <div className="col">
          <div className="float-right">
            <div className="input-group input-group-lg mb-2">
              <span className="input-group-text" data-testid="duration-break">
                {/* TODO: Update this text to display the current break session duration */}
                Break Duration: {secondsToDuration(sessionStates.breakTimer)}
              </span>
              <div className="input-group-append">
                {/* TODO: Implement decreasing break duration and disable during a focus or break session*/}
                <button
                  type="button"
                  className="btn btn-secondary"
                  data-testid="decrease-break"
                  onClick={() => decreaseSessionClick("breakTimer", 60)}
                >
                  <span className="oi oi-minus" />
                </button>
                {/* TODO: Implement increasing break duration and disable during a focus or break session*/}
                <button
                  type="button"
                  className="btn btn-secondary"
                  data-testid="increase-break"
                  onClick={() => increaseSessionClick("breakTimer", 900)}
                >
                  <span className="oi oi-plus" />
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>
      <div className="row">
        <div className="col">
          <div
            className="btn-group btn-group-lg mb-2"
            role="group"
            aria-label="Timer controls"
          >
            <button
              type="button"
              className="btn btn-primary"
              data-testid="play-pause"
              title="Start or pause timer"
              onClick={playPause}
            >
              <span
                className={classNames({
                  oi: true,
                  "oi-media-play": !isTimerRunning,
                  "oi-media-pause": isTimerRunning,
                })}
              />
            </button>
            {/* TODO: Implement stopping the current focus or break session and disable when there is no active session */}
            <button
              type="button"
              className="btn btn-secondary"
              title="Stop the session"
              onClick={stopSession}
            >
              <span className="oi oi-media-stop" />
            </button>
          </div>
        </div>
      </div>
      <div>
        <div className="row mb-2">
          <div className="col">
           <SessionTracker 
           sessionStatus={sessionStates.sessionStatus} 
           focusTimer={sessionStates.focusTimer} 
           breakTimer={sessionStates.breakTimer} 
           timeRemaining={sessionStates.timeRemaining}
           isStopped={sessionStates.isStopped} />
          </div>
        </div>
        <PausedTimer 
        isStopped={sessionStates.isStopped}
        isTimerRunning={isTimerRunning}/>
        <div className="row mb-2">
          <div className="col">

            < PercentageBar 
            elapsed={sessionStates.elapsed} 
            sessionStatus={sessionStates.sessionStatus} 
            focusTimer={sessionStates.focusTimer} 
            breakTimer={sessionStates.breakTimer}
            isStopped={sessionStates.isStopped} />  

          </div>
        </div>
      </div>
    </div>
  );
}

export default Pomodoro;
