function SessionRunning({ focusTimer, breakTimer, activeTimer, elapsed, timeRemaining, sessionStatus }) {
  //Set object to be updated every second and returned to update the states
  let sessionStatuses = {
    elapsed: elapsed,
    timeRemaining: timeRemaining,
    sessionStatus: sessionStatus,
    focusTimer: focusTimer,
    breakTimer: breakTimer,
    activeTimer: activeTimer
  };

  //Check that the session status is set to Focus
  if (sessionStatuses.sessionStatus === "Focusing") {

    //Check that the time elapsed is still less than the timer set
    if (sessionStatuses.elapsed < sessionStatuses.activeTimer) {

      //Add 1 second to elapsed
      sessionStatuses.elapsed = sessionStatuses.elapsed + 1;

      //Update timeRemaining with focusTimer-elapsed
      sessionStatuses.timeRemaining = sessionStatuses.activeTimer - sessionStatuses.elapsed;
      
      //Return the updated sessionStatuses
      return sessionStatuses;

    } else {
      
      //Focus timer is up, switch status to Break, timeRemaining to breakTimer, and set elapsed to 0
      sessionStatuses.sessionStatus = "Break";

      sessionStatuses.timeRemaining = sessionStatuses.breakTimer;

      sessionStatuses.elapsed = 0;

      sessionStatuses.activeTimer = sessionStatuses.breakTimer;

      return sessionStatuses;
    }
  } else {
