      sessionStatuses.timeRemaining = sessionStatuses.focusTimer;

      sessionStatuses.elapsed = 0;

      sessionStatuses.activeTimer = sessionStatuses.focusTimer;

      return sessionStatuses;
    };
  };
};

export default SessionRunning;
