import { minutesToDuration, secondsToDuration}  from "../../utils/duration";

export const checkMax = (num, max) => {
    if (num < max ) {
      return 60;
    } else {
      return 0;
    };
  };

export const checkMin = (num, min) => {
      if (num > min) {
          return 60;
      } else {
          return 0;
      };
  };

export const checkHour = (num) => {
  if (num < 3600) {
    return secondsToDuration(num);
  } else {
    return minutesToDuration(3600);
  };
};
