import React from "react";
import { checkHour } from "./sideFunctions/MathEquations";
import { secondsToDuration } from "../../utils/duration";
import ActiveSessionCheck from "./sideFunctions/ActiveSessionCheck";

function SessionTracker({ sessionStatus, focusTimer, breakTimer, timeRemaining }) {
    return (
        <>
        <h2 data-testid="session-title">{sessionStatus} for {checkHour(ActiveSessionCheck(sessionStatus, focusTimer, breakTimer))} minutes</h2>
        <p className="lead" data-testid="session-sub-title">
        {secondsToDuration(timeRemaining)} remaining
        </p>
        </>
    )
};


export default SessionTracker;
