import { minutesToDuration, secondsToDuration } from "../../utils/duration";

export const checkMax = (num, max) => {
    if (num < max ) {
      return secondsToDuration(num+60);
    } else {
      return minutesToDuration(num+0);
    };
  };

export const checkMin = (num, min) => {
      if (num > min) {
          return 60;
      } else {
          return 0;
      };
  };
