import React, { useState } from "react";
import classNames from "../utils/class-names";
import useInterval from "../utils/useInterval";
import { checkMaxFocus, checkMaxBreak, checkMinFocus, checkMinBreak, checkHour } from "./sideFunctions/MathEquations";
import { secondsToDuration } from "../utils/duration";
import SessionRunning from "./sideFunctions/FocusTimer";
import PercentageBar from "./components/percentage-bar";
import SessionTracker from "./components/session-tracker";
import PausedTimer from "./components/paused";
import { disableWhenStopped, disableWhenRunning } from "./sideFunctions/ButtonDisablers";


function Pomodoro() {
  // Timer starts out paused

  //Click handlers
  const increaseFocusSessionClick = (value, max) => {
    setSessionStates({
      ...sessionStates,
      [value]: sessionStates[value] + checkMaxFocus(sessionStates[value], max)
  });
 };

  const decreaseFocusSessionClick = (value, min) => {
    setSessionStates({
      ...sessionStates,
      [value]: sessionStates[value] - checkMinFocus(sessionStates[value], min)
    });
  };

  const increaseBreakSessionClick = (value, max) => {
    setSessionStates({
      ...sessionStates,
      [value]: sessionStates[value] + checkMaxBreak(sessionStates[value], max)
  });
 };

  const decreaseBreakSessionClick = (value, min) => {
    setSessionStates({
      ...sessionStates,
      [value]: sessionStates[value] - checkMinBreak(sessionStates[value], min)
    });
  };

  const stopSession = () => {
    setSessionStates({
      ...initialSessionState
    });
    setIsTimerRunning(false)
  };


  //State object variable
  const initialSessionState = {
    focusTimer: 1500,
    breakTimer: 300,
    elapsed: 0,
    timeRemaining: 1500,
    sessionStatus: "Focusing",
    isStopped: true
  };

  const [sessionStates, setSessionStates] = useState(initialSessionState);
  const [isTimerRunning, setIsTimerRunning] = useState(false);


  useInterval(
    () => {
      // Function that updates elapsed, remaining, and session status
        // Will return object to update sessionStates state object
      const updatedSessionStates = SessionRunning(sessionStates);

      setSessionStates({
        ...sessionStates,
        ...updatedSessionStates
      })
    },
    isTimerRunning ? 1000 : null
  );

  function playPause() {
    setIsTimerRunning((prevState) => !prevState);
    setSessionStates({
      ...sessionStates,
      isStopped: false
    });
  };

  return (
    <div className="pomodoro">
      <div className="row">
        <div className="col">
          <div className="input-group input-group-lg mb-2">

            <span className="input-group-text" data-testid="duration-focus">
              Focus Duration: {checkHour(sessionStates.focusTimer)}
            </span>

            <div className="input-group-append">

              <button
                type="button"
                className="btn btn-secondary"
                data-testid="decrease-focus"
                onClick={() => decreaseFocusSessionClick("focusTimer", 300)}
                {...disableWhenRunning(sessionStates.isStopped)}
              >
                <span className="oi oi-minus" />
              </button>

              <button
                type="button"
                className="btn btn-secondary"
                data-testid="increase-focus"
                onClick={() => increaseFocusSessionClick("focusTimer", 3600)}
                {...disableWhenRunning(sessionStates.isStopped)}
              >
                <span className="oi oi-plus" />
              </button>

            </div>
          </div>
        </div>
        <div className="col">
          <div className="float-right">
            <div className="input-group input-group-lg mb-2">

              <span className="input-group-text" data-testid="duration-break">
                Break Duration: {secondsToDuration(sessionStates.breakTimer)}
              </span>

              <div className="input-group-append">

                <button
                  type="button"
                  className="btn btn-secondary"
                  data-testid="decrease-break"
                  onClick={() => decreaseBreakSessionClick("breakTimer", 60)}
                  {...disableWhenRunning(sessionStates.isStopped)}
                >
                  <span className="oi oi-minus" />
                </button>

                <button
                  type="button"
                  className="btn btn-secondary"
                  data-testid="increase-break"
                  onClick={() => increaseBreakSessionClick("breakTimer", 900)}
                  {...disableWhenRunning(sessionStates.isStopped)}
                >
                  <span className="oi oi-plus" />
                </button>

              </div>
            </div>
          </div>
        </div>
      </div>
      <div className="row">
        <div className="col">
          <div
            className="btn-group btn-group-lg mb-2"
            role="group"
            aria-label="Timer controls"
          >
            <button
              type="button"
              className="btn btn-primary"
              data-testid="play-pause"
              title="Start or pause timer"
              onClick={playPause}
            >
              <span
                className={classNames({
                  oi: true,
                  "oi-media-play": !isTimerRunning,
                  "oi-media-pause": isTimerRunning,
                })}
              />
            </button>

            <button
              type="button"
              className="btn btn-secondary"
              title="Stop the session"
              onClick={stopSession}
              {...disableWhenStopped(sessionStates.isStopped)}
            >
              <span className="oi oi-media-stop" />
            </button>

          </div>
        </div>
      </div>
      <div>
        <div className="row mb-2">
          <div className="col">

           <SessionTracker 
           sessionStatus={sessionStates.sessionStatus} 
           focusTimer={sessionStates.focusTimer} 
           breakTimer={sessionStates.breakTimer} 
           timeRemaining={sessionStates.timeRemaining}
           isStopped={sessionStates.isStopped} />

          </div>
        </div>

        <PausedTimer 
        isStopped={sessionStates.isStopped}
        isTimerRunning={isTimerRunning}/>

        <div className="row mb-2">
          <div className="col">

            < PercentageBar 
            elapsed={sessionStates.elapsed} 
            sessionStatus={sessionStates.sessionStatus} 
            focusTimer={sessionStates.focusTimer} 
            breakTimer={sessionStates.breakTimer}
            isStopped={sessionStates.isStopped} />  

          </div>
        </div>
      </div>
    </div>
  );
}

export default Pomodoro;
